import { useRef } from 'react';
import classes from  './checkout.module.css';

const Checkoutform=(props)=>{

    const nameinputref=useRef();
    const addressinputref=useRef();
    const pincodeinputref=useRef();
    const cityinputref=useRef();
   

    const isempty=(value)=>{return value.trim() === ''}
    const isfivechar=(value)=>{return value.trim().length===5}

    const nameinputclasses=nameinputisvalid?'control':'invalid'
    const addressinputclasses=addressinputisvalid?'control':'invalid'
    const pincodeinputclasses=pincodeinputisvalid?'control':'invalid'
    const cityinputclasses=cityinputisvalid?'control':'invalid'

   const confirmhandler=(event)=>{
    event.preventDefault();

    const enteredname= nameinputref.current.value;
    const enteredaddress= addressinputref.current.value;
    const enteredpincode= pincodeinputref.current.value;
    const enteredcity= cityinputref.current.value;

    const nameinputisvalid=isempty(enteredname);
    const addressinputisvalid=isempty(enteredaddress);
    const pincodeinputisvalid=isfivechar(enteredpincode);
    const cityinputisvalid=isempty(enteredcity);
   }
    return(
        <form className={classes.form} onSubmit={confirmhandler}>
        <div className={nameinputclasses}>
        <label htmlFor="name">Your Name</label>
        <input type='text' id='name' ref={nameinputref} ></input>
        </div>
        <div className={addressinputclasses}>
        <label htmlFor="address">Address</label>
        <input type='text' id='address' ref={addressinputref} ></input>
        </div>
        <div className={pincodeinputclasses}>
        <label htmlFor="Pincode">Pincode</label>
        <input type='text' id='Pincode' ref={pincodeinputref} ></input>
        </div>
        <div className={cityinputclasses}>
        <label htmlFor="City">City</label>
        <input type='text' id='City' ref={cityinputref}></input>
        </div>
        <div className={classes.actions}>
        <button onClick={props.oncancel}>Cancel</button>
        <button className={classes.submit}>Confirm</button>
        </div>
        </form>

    )
}
export default Checkoutform;
